import React, { useRef, useEffect } from "react";
import { Provider, Heading, Subhead } from "rebass";
import { Hero, Flex, CallToAction, ScrollDownIndicator } from "react-landing-page";
import { throttle } from "lodash";
import Home from "./Home";

const Landing = () => {
  const scrollToRef = useRef(null);
  const backgroundRef = useRef(null);

  const handleScroll = throttle(() => {
    const scrollY = window.scrollY || window.pageYOffset;
    if (backgroundRef.current) {
      backgroundRef.current.style.transform = `translate3d(0, ${scrollY * 0.4}px, 0)`;
    }
  }, 16);

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [handleScroll]);

  const handleClick = () => {
    if (scrollToRef.current) {
      scrollToRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <>
      <Provider>
        <Hero color="white" bg="black" bgOpacity={0.1} minHeight="100vh" style={{ position: "relative" }}>
          <div
            style={{ position: "absolute", top: 0, left: 0, width: "100%", height: "100%", overflow: "hidden" }}
            ref={backgroundRef}
          >
            <img
              src="https://wallpapers.com/images/high/verdant-rice-fields-and-farm-surrounding-small-village-8uuv5wg8j0tqwpo5.webp"
              alt="Parallax Background"
              style={{ width: "100%", height: "100%", objectFit: "cover" }}
            />
          </div>
          <Flex flexDirection="column" alignItems="center" justifyContent="center" height="100%">
            <Heading fontSize={[5, 6, 7]} textAlign="center" zIndex={1}>
              Tomato Leaf Prediction
            </Heading>
            <Subhead fontSize={[2, 3, 4]} textAlign="center" mt={3} zIndex={1}>
              Check If Your Plant is Healthy
            </Subhead>
            <Flex mt={4} justifyContent="center" zIndex={1}>
              <CallToAction bg="grey" mr={3} onClick={handleClick}>
                Start Now
              </CallToAction>
              <CallToAction bg="white" color="black" onClick={handleClick}>
                Learn More
              </CallToAction>
            </Flex>
            <ScrollDownIndicator onClick={handleClick} mt={4} color="white" zIndex={1} />
          </Flex>
        </Hero>
      </Provider>
      <div ref={scrollToRef}>
        <Home />
      </div>
    </>
  );
};

export default Landing;
