import React, { useRef, useEffect } from "react";
import "tachyons";
import { Provider, Heading, Subhead } from "rebass";
import { Hero, ScrollDownIndicator } from "react-landing-page";
import Home from "./home";
import { CssBaseline, useTheme } from "@material-ui/core";

const Landing = () => {
  const homeRef = useRef(null);
  const theme = useTheme();

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY === 0) {
        homeRef.current.scrollIntoView({ behavior: "smooth" });
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <React.Fragment>
      <CssBaseline />
      <Provider>
        <Hero
          color={theme.palette.text.primary}
          backgroundImage="https://images.unsplash.com/photo-1498050108023-c5249f4df085"
          bg="primary"
          bgOpacity={0.6}
        >
          <Heading as="h1" fontSize={[6, 7, 8]} mb={3}>
            Welcome to Tomato Leaf Prediction
          </Heading>
          <Subhead fontSize={[2, 3, 4]} mb={4}>
            Use our AI model to identify the disease in your tomato plants
          </Subhead>
          <ScrollDownIndicator
            style={{ marginTop: "3rem" }}
            onClick={() => homeRef.current.scrollIntoView({ behavior: "smooth" })}
          />
        </Hero>
      </Provider>
      <div ref={homeRef}>
        <Home />
      </div>
    </React.Fragment>
  );
};

export default Landing;
